package org.example.reminder1.config;import lombok.RequiredArgsConstructor;import org.example.reminder1.service.CustomOAuth2UserService;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.security.config.annotation.web.builders.HttpSecurity;import org.springframework.security.config.annotation.web.configuration.EnableWebSecurity;import org.springframework.security.web.SecurityFilterChain;@Configuration@EnableWebSecurity@RequiredArgsConstructorpublic class SecurityConfig {    private final CustomOAuth2UserService customOAuth2UserService;    @Bean    public SecurityFilterChain filterChain(HttpSecurity http) throws Exception {        http.csrf(csrf -> csrf.disable())                .authorizeHttpRequests(auth -> auth                        .requestMatchers("/api/v1/*/test/**").permitAll()                        .anyRequest().authenticated()                )                .oauth2Login(oauth2 -> oauth2                        .userInfoEndpoint(userInfoEndpointConfig -> userInfoEndpointConfig                                .userService(customOAuth2UserService))                        .defaultSuccessUrl("/api/v1/auth/success", true)                );        return http.build();    }}